import React, { useEffect, useState } from 'react'
import './AddScenario.css'

const AddScenario = () => {

  const [data, setdata]=useState({
    id:'',
    name:'',
    seconds:''
  });

  const [name, setname]=useState
  const [seconds, setseconds]=useState



  const Handleform=(e)=>{
    e.preventDefault();
   
    
    let data=([...data,setdata])
    
  }

  useEffect(()=>{
    localStorage.setItem('data',JSON.stringify(data));
  },[data])
  

  return (
    <div className='container'>
      <div><h3>Scenario/add</h3></div>
      <div><h1>Add Scenario</h1></div>
      <form className='box'>
      <div className='subbox'>
          <label>Scenario Name</label>
          <br></br>
          <input type='text'name='name' placeholder='Test Scenario' onChange={(e)=>(setname(e.target.value))} required></input>
        </div>
        <div className='subbox'>
          <label>Scenario Time(seconds)</label>
          <br></br>
          <input type='number' name='second' placeholder='10' onChange={(e)=>(setseconds(e.target.value))} required></input>
        </div>
        <div>
        <button className='btnadd' >Add</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btnreset'>Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btngoback'>Go Back</button>
        </div>
      </form>
      <br></br>
      
      
      
    </div>
  )
}

export default AddScenario
