import React, { useState } from 'react'
import { NavLink } from 'react-router-dom'
import './AllScenarios.css'

const AllScenarios = () => {

const [data,setdata]=useState((JSON.parse(localStorage.getItem('scenarios'))))




  
const Handledelete=(a)=>{

 
 
    const Udata = data.filter((ele,i) => {
    return i !==a;
      
      
    });
    
    setdata(Udata)
    localStorage.setItem('scenarios',JSON.stringify(Udata))
  }



  

 

  
  return (
    <div className='container'>
      
      
      <h1>Scenario</h1><div className='btn'>
        <button className='btnadd'>Add</button><button className='btnreset'>Reset</button><button className='btngoback'>Go Back</button>
      </div>
      <div>
        <table className='mytable'>
          <thead>
            <tr>
              <th>Scenario Id</th>
              <th>Scenario Name</th>
              <th>Scenario Time</th>
              <th>Number of Vehicle</th>
              <th>Add Vehicle</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          
            {data.map((data,i)=>(
              <tr>
                <td>{1-i}</td>
                <td>{data.name}</td>
                <td>{data.seconds}</td>
                <td>{data.seconds}</td>
                
                <td><NavLink to='/addscenario'><button 
                onClick={(i)=>{
                  data(setdata[i])
                }}
                
                >Edit</button></NavLink></td>


                <td><button id={data.id} onClick={()=>Handledelete(i)}>Delete</button></td>
                <div></div>
              </tr>
           
            ))}
          
        </table>
      </div>
      
      
      </div>
  )
}


export default AllScenarios
