import React, { useEffect, useState } from 'react'
import './AddScenario.css'

const AddScenario = () => {

  const [contactInfo, setContactInfo] = useState({
    name: "",
    email: "",
    phonenumber: "",
  });

  const [name, setname]=useState('');
  const [seconds, setseconds]=useState('');

  const Handleform=(e)=>{
    e.preventDefault();
   
    let store={
    
      name,
      seconds
    }
    setdata([...data,store])
    setname('')
    setseconds('')
  }

  useEffect(()=>{
    localStorage.setItem('data',JSON.stringify(data));
  },[data])
  

  return (
    <div className='container'>
      <div><h3>Scenario/add</h3></div>
      <div><h1>Add Scenario</h1></div>
      <form className='box'>
      <div className='subbox'>
          <label>Scenario Name</label>
          <br></br>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={data.name}
          />
        </div>
        <div className='subbox'>
          <label>Scenario Time(seconds)</label>
          <br></br>
          <input
            type="number"
            name="seconds"
            placeholder="Name"
            value={data.seconds}
          />
        </div>
      </form>
      <br></br>
      <button className='btnadd' onClick={Handleform}>Add</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btnreset'>Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btngoback'>Go Back</button>
      
      
    </div>
  )
}

export default AddScenario
