import React, { useState } from 'react'
import { NavLink } from 'react-router-dom'
import './home.css'

const Home = () => {

  const [data,setdata]=useState((JSON.parse(localStorage.getItem('vehicle'))))
  
  
  const scenarios=localStorage.getItem('scenarios')
  const [Scenarios]=useState(scenarios ==null? [] : JSON.parse(scenarios))
 


  const handledelete=(index)=> {
    data.splice(index,1)
    setdata([...data])
   }


  

  return (
    <div className='container'>AllScenarios<br></br>
    <select onClick={(e)=>{
            const setselectscenario=e.target.value
            console.log(E.target)
            if(setselectscenario==='test scenario'){
             
                console.log('Show')
              
              
              
              
              }
              else{
                console.log('hi')
              }
          }}>
          <option>select Direction</option>
          
          <option value='test scenario' > Test Scenario</option>
          <option value='My Scenario'> My Scenario</option>
          
</select>
    <div>
    <table className='mytable'>
          <thead>
            <tr>
              <th>Vehicle Id</th>
              <th>Vehicle Name</th>
              <th>Position X</th>
              <th>Position Y</th>
              <th>Speed</th>
              <th>Direction</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>

          
        
    {data.map((data,i)=>(
      <tr>
        <td>{1-i}</td>
        <td>{data.vname}</td>
        <td>{data.x}</td>
        <td>{data.y}</td>
        <td>{data.speed}</td>
        <td>{data.position}</td>
        <td><NavLink to='/addscenario'><button 
        onClick={(i)=>{
          data(setdata[i])
        }}
        
        >Edit</button></NavLink></td>
        <td><button onClick={handledelete}>Delete</button></td>
        <div></div>
      </tr>
   
    ))}
  
   
          
        </table>
    </div>
    </div>
  )
}

export default Home
