import React, { useEffect, useState } from 'react'
import './AddScenario.css'

const AddScenario = () => {



  const [contacts, setContacts] = useState(data);
const [addFormData, setAddFormData] = useState({
  name: "",
  time: "",
});


const handleAddFormChange = (event) => {
  event.preventDefault();

  const fieldName = event.target.getAttribute("name");
  const fieldValue = event.target.value;

  const newFormData = { ...addFormData };
  newFormData[fieldName] = fieldValue;

  setAddFormData(newFormData);
};

const handleAddFormSubmit = (event) => {
  event.preventDefault();

  const newContact = {
    id: nanoid(),
    fullName: addFormData.fullName,
    address: addFormData.address,
    phoneNumber: addFormData.phoneNumber,
    email: addFormData.email,
  };

  const newContacts = [...contacts, newContact];
  setContacts(newContacts);
};



  return (
    <div className='container'>
      <div><h3>Scenario/add</h3></div>
      <div><h1>Add Scenario</h1></div>
      <form className='box'>
      <div className='subbox'>
          <label>Scenario Name</label>
          <br></br>
          <input
          type="text"
          name="name"
          required="required"
          placeholder="Enter a phone number..."
          onChange={handleAddFormChange}
        />
        </div>
        <div className='subbox'>
          <label>Scenario Time(seconds)</label>
          <br></br>
          <input
          type="number"
          name="time"
          required="required"
          placeholder="Enter a phone number..."
          onChange={handleAddFormChange}
        />
        </div>
      </form>
      <br></br>
      <button className='btnadd' onClick={handleAddFormSubmit}>Add</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btnreset'>Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btngoback'>Go Back</button>
      
      
    </div>
  )
}

export default AddScenario
