  useEffect(()=>{
    localStorage.setItem('data',JSON.stringify(data));
  },[data])
  

  return (
    <div className='container'>
      <div><h3>Scenario/add</h3></div>
      <div><h1>Add Scenario</h1></div>
      <form className='box'>
      <div className='subbox'>
          <label>Scenario Name</label>
          <br></br>
          <input
          type="text"
          name="name"
          required="required"
          placeholder="Enter a phone number..."
          onChange={handleAddFormChange}
        />
        </div>
        <div className='subbox'>
          <label>Scenario Time(seconds)</label>
          <br></br>
          <input
          type="number"
          name="time"
          required="required"
          placeholder="Enter a phone number..."
          onChange={handleAddFormChange}
        />
        </div>
      </form>
      <br></br>
      <button className='btnadd' onClick={Handleform}>Add</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btnreset'>Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btngoback'>Go Back</button>
      
      
    </div>
  )
}

export default AddScenario
