import React, { useEffect, useState } from 'react'
import './AddScenario.css'
import { v4 as uuidv4 } from 'uuid';

const AddScenario = () => {

const [data, setdata]=useState([]);

  const [name, setname]=useState('');
  const [seconds, setseconds]=useState('');

  const Handleform=(e)=>{
    
    if(name.length!==0){
      e.preventDefault();
      Call()
    }
  }
    const Call=()=>{
      const storageScenarios=localStorage.getItem('scenarios')
      
      const store={
        name,
        seconds,
        id:uuidv4()
      }
      setdata([...data,store])
      setname("");
      setseconds("");
    }
    
    localStorage.setItem('data',JSON.stringify(data));
    
  return (
    <div className='container'>
      <div><h3>Scenario/add</h3></div>
      <div><h1>Add Scenario</h1></div>
      <form className='box'>
      <div className='subbox'>
          <label>Scenario Name</label>
          <br></br>
          <input type='text' value={name} placeholder='Test Scenario' onChange={(e)=>(setname(e.target.value))} required></input>
        </div>
        <div className='subbox'>
          <label>Scenario Time(seconds)</label>
          <br></br>
          <input type='number'value={seconds} placeholder='10' onChange={(e)=>(setseconds(e.target.value))} required></input>
        </div>
      </form>
      <br></br>
      <button className='btnadd' onClick={Handleform}>Add</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btnreset'>Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btngoback'>Go Back</button>
      
      
    </div>
  )
}

export default AddScenario
