import React, { useEffect, useState } from 'react'
import './AddVehicle.css'

const AddVehicle = () => {
 
  const [datas, setdatas]=useState([]);


  const [vname, setvname]=useState('')
  const [speed, setspeed]=useState('')
  const [x, setx]=useEffect('')
  const [y, sety]=useEffect('')

  const data=((JSON.parse(localStorage.getItem('data'))))

  const Handleform=(e)=>{
    
    if(x.length!==0){
      e.preventDefault();
      Call()
    }
  }

    const Call=()=>{
      
      const store={
        vname,
        speed,
        x,
        y,
      }
      setdata([...datas,store])
      setname('');
      setseconds("");
    }



    useEffect(()=>{
      localStorage.setItem('vdata',JSON.stringify(data));
    },[data])


  return (
    <div className='container'>
      <div><h3>Vehicle/add</h3></div>
      <div><h1>Add Vehicle</h1></div>
      <div className='box'>
      <div className='subbox'>
          <label>Scenario List</label>
          <br></br>
          
          
          
          <select>
          <option >select Direction</option>
          {data.map((data,i)=>(
          <option >{data.name}</option>
          
))}    </select>
        </div>
        <div className='subbox'>
          <label>Vehicle Name</label>
          <br></br>
          <input type='text'value={vname} placeholder='10' onChange={(e)=>(setvname(e.target.value))} required></input>
        </div>
        <div className='subbox'>
          <label>Speed</label>
          <br></br>
          <input type='number'value={speed} placeholder='10' onChange={(e)=>(setspeed(e.target.value))} required></input>
        </div>
      </div>

      <div className='box'>
      <div className='subbox'>
          <label>Position X</label>
          <br></br>
          <input type='number'value={x} placeholder='10' onChange={(e)=>(setx(e.target.value))} required></input>
        </div>
        <div className='subbox'>
          <label>Position Y</label>
          <br></br>
          <input type='number'value={y} placeholder='10' onChange={(e)=>(sety(e.target.value))} required></input>
        </div>
        <div className='subbox'>
          <br></br>
          <select>
          <option value="">select Direction</option>
          <option value="2">Towards</option>
          <option value="3">Backwards</option>
          <option value="4">upwards</option>
          <option value="5">Downwards</option>
          </select>
          
        </div>
      </div>

      
      
      <br></br>
      <button className='btnadd'>Add</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btnreset'>Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;<button className='btngoback'>Go Back</button>
      
      
    </div>
  )
}

export default AddVehicle
