import React, { useState } from "react";
import "./AddScenario.css";
import { useNavigate, useParams } from "react-router-dom";

const EditScenario = () => {
  const navigate = useNavigate();

  let { id } = useParams();

  console.log(id);

  const [edit] = useState(JSON.parse(localStorage.getItem("scenarios")));
  
  const Handle = () => {
    let newedit=edit.find((ele)=>{
    const mom=ele.id===id;
    console.log(mom)
    console.log(newedit)
    const [newedit]=useState(newedit)
  })}
 
  

  const Handleform = () => {};

  return (
    <div className="container">
      <div>
        <h3>Scenario/Edit</h3>
      </div>
      <div>
        <h1>Edit Scenario</h1>
      </div>
      <div className="box">
        <div className="subbox">
          <label>Scenario Name</label>
          <br></br>
          {newedit.map(newedit=>
          <input onClick={Handle} value={newedit.name}></input>
            )}
          
          <input
            type="text"
            
           
            
            required
          ></input>
        </div>
        <div className="subbox">
          <label>Scenario Time(seconds)</label>
          <br></br>
          <input
            type="number"
            
            placeholder="10"
            
            required
          ></input>
        </div>
        <div></div>
      </div>
      <br></br>
      <button className="btnadd" onClick={Handleform}>
        Update
      </button>
    </div>
  );
};

export default EditScenario;
